CREATE TRIGGER t_vende_item AFTER INSERT ON itens_venda FOR EACH ROW
BEGIN
	UPDATE controle_estoque SET quantidade = quantidade - NEW.quantidade WHERE id_produto_controle = NEW.id_produto_itenvenda;
